{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyNJkO9cPpFG+t48VFySY3Sd"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","source":["import pandas as pd\n","from sklearn.model_selection import train_test_split\n","\n","RANDOM_STATE = 42\n","TARGET_COL = \"default.payment.next.month\"\n","\n","def load_credit_data(csv_path: str):\n","    \"\"\"\n","    Charge le dataset de risque de crédit.\n","    Supprime les colonnes purement identifiantes (ex: ID).\n","    Sépare X (features) et y (cible binaire).\n","    \"\"\"\n","    df = pd.read_csv(csv_path)\n","\n","    # Suppression de la colonne ID (variable sans information prédictive)\n","    if \"ID\" in df.columns:\n","        df = df.drop(columns=[\"ID\"])\n","\n","    X = df.drop(columns=[TARGET_COL])\n","    y = df[TARGET_COL].astype(int)\n","\n","    return X, y\n","\n","\n","def make_split(X, y, test_size=0.2):\n","    \"\"\"\n","    Création d'un split train/test stratifié.\n","    La stratification permet de conserver la proportion\n","    de défaut dans les deux sous-échantillons.\n","    \"\"\"\n","    return train_test_split(\n","        X, y,\n","        test_size=test_size,\n","        random_state=RANDOM_STATE,\n","        stratify=y\n","    )"],"metadata":{"id":"31qtyd4a0qan","executionInfo":{"status":"ok","timestamp":1772092016530,"user_tz":-480,"elapsed":2466,"user":{"displayName":"Céline XIE","userId":"09348543885754058302"}}},"execution_count":1,"outputs":[]}]}